import { Component, OnInit } from '@angular/core';
import { ProductsService } from '../../products.service';
import { Router } from '@angular/router';
import { MatDialog } from '@angular/material/dialog';
import { ProductsDeleteComponent } from '../products-delete/products-delete.component';

@Component({
  selector: 'app-products-home',
  templateUrl: './products-home.component.html',
  styleUrls: ['./products-home.component.css']
})
export class ProductsHomeComponent implements OnInit {
  dataSource: any = [];

  constructor(
    private productService: ProductsService,
    private route: Router,
    public dialog: MatDialog
  ) { }

  ngOnInit(): void {
    this.productService.getProducts().subscribe(data => {
      this.dataSource = data;
    });
  }

  OpenDetailForm(row:any){
    this.route.navigate(['/products', row.id]);
  }
  openDeleteDialog(contactId: number):void{
    const dialogRef = this.dialog.open(ProductsDeleteComponent, { data: {contactId : contactId}})
  }

  openEdit(id: any){
    this.route.navigate(['/products/edit', id]);
  }
}
<a mat-stroked-button routerLink="/products/new" id="new-contact"> Nuevo Producto</a>
<table mat-table [dataSource]="dataSource" class="mat-elevation-z8 card">

  <ng-container matColumnDef="id">
    <th mat-header-cell *matHeaderCellDef> ID </th>
    <td mat-cell *matCellDef="let product"> {{product.id}} </td>
  </ng-container>

  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef> Name </th>
    <td mat-cell *matCellDef="let product"> {{product.name}} </td>
  </ng-container>

  <ng-container matColumnDef="stock">
    <th mat-header-cell *matHeaderCellDef> Stock </th>
    <td mat-cell *matCellDef="let product"> {{product.stock}} </td>
  </ng-container>

  <ng-container matColumnDef="price">
    <th mat-header-cell *matHeaderCellDef> Precio </th>
    <td mat-cell *matCellDef="let product"> {{product.price}} </td>
  </ng-container>

  <ng-container matColumnDef="active">
    <th mat-header-cell *matHeaderCellDef> Activo </th>
    <td mat-cell *matCellDef="let product"> {{ product.active}} </td>
  </ng-container>

  <ng-container matColumnDef="date_added">
    <th mat-header-cell *matHeaderCellDef> Fecha </th>
    <td mat-cell *matCellDef="let product"> {{product.date_added | date : 'dd/MM/yyyy'}} </td>
  </ng-container>
  <ng-container matColumnDef="actions">
    <th mat-header-cell *matHeaderCellDef> Accion </th>
    <td mat-cell *matCellDef="let product">
      <button mat-icon-button color="primary" (click)="openEdit(product.id); $event.stopPropagation()" >
        <mat-icon>edit</mat-icon>
      </button>
      <button mat-icon-button color="warn" (click)="openDeleteDialog(product.id); $event.stopPropagation()" >
        <mat-icon>delete</mat-icon>
      </button>
    </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="['name', 'stock', 'price', 'active', 'date_added', 'actions']"></tr>
  <tr mat-row *matRowDef="let row; columns: ['name', 'stock', 'price', 'active', 'date_added', 'actions'];"
  (click)="OpenDetailForm(row)"></tr>
</table>
